import React, { useState } from "react";
import {
  ButtonDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  Alert,
} from "reactstrap";

function ReactstrapDropdown() {
  const [toggleOpen, setToggleOpen] = useState(false);

  function ToggleOpen() {
    setToggleOpen(!toggleOpen);
  }

  function Mission() {}

  return (
    <>
      <Alert color="danger">
        미션 : 아래의 Dropdown 메뉴에서 "미션 수행"을 클릭해보세요.
      </Alert>
      <ButtonDropdown isOpen={toggleOpen} toggle={ToggleOpen}>
        <DropdownToggle caret> 버튼 Dropdown</DropdownToggle>
        <DropdownMenu>
          <DropdownItem header>헤더</DropdownItem>
          <DropdownItem disabled>비활성화 버튼</DropdownItem>
          <a href="http://example.com/">
            <DropdownItem>example 웹 사이트로 이동</DropdownItem>
          </a>
          <DropdownItem onClick={Mission}>미션 수행</DropdownItem>
        </DropdownMenu>
      </ButtonDropdown>
    </>
  );
}

export default ReactstrapDropdown;
