import React, { useState } from "react";
import {
  ButtonDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  UncontrolledAlert,
} from "reactstrap";

function ReactstrapDropdown() {
  const [open, setOpen] = useState(false);
  const [success, setSuccess] = useState(true);

  return (
    <>
      <UncontrolledAlert isOpen={success} color="danger">
        UncontrolledAlert
      </UncontrolledAlert>
      <ButtonDropdown isOpen={open} toggle={() => setOpen(!open)}>
        <DropdownToggle caret> 버튼 Dropdown</DropdownToggle>
        <DropdownMenu>
          <DropdownItem header>헤더</DropdownItem>
          <DropdownItem disabled>비활성화 버튼</DropdownItem>
          <a href="http://example.com/">
            <DropdownItem>example 웹 사이트로 이동</DropdownItem>
          </a>
          <DropdownItem onClick={(e) => alert("Alert 버튼")}>
            Alert 버튼
          </DropdownItem>
        </DropdownMenu>
      </ButtonDropdown>
    </>
  );
}

export default ReactstrapDropdown;
