import React, { useState } from "react";
import {
  ButtonDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  Alert,
} from "reactstrap";

function AlertAndDropdown() {
  const [toggleOpen, setToggleOpen] = useState(false);
  const [alertColor, setAlertColor] = useState("danger");
  const [alertText, setAlertText] = useState(
    "미션 : Dropdown에서 [미션 수행] 클릭하기."
  );

  function ToggleOpen() {
    setToggleOpen(!toggleOpen);
  }

  function Mission() {
    setAlertColor("success");
    setAlertText("미션 성공!");
  }

  return (
    <>
      <Alert color={alertColor} style={"width : 100px"}>
        {alertText}
      </Alert>
      <ButtonDropdown isOpen={toggleOpen} toggle={ToggleOpen}>
        <DropdownToggle caret> Dropdown</DropdownToggle>
        <DropdownMenu>
          <DropdownItem header>헤더</DropdownItem>
          <DropdownItem disabled>비활성화 버튼</DropdownItem>
          <a href="http://example.com/">
            <DropdownItem>example 웹 사이트로 이동</DropdownItem>
          </a>
          <DropdownItem onClick={Mission}>미션 수행</DropdownItem>
        </DropdownMenu>
      </ButtonDropdown>
    </>
  );
}

export default AlertAndDropdown;
